// SPDX-License-Identifier: MIT

pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./DummyDeRace.sol";

contract Academy is Ownable {
  using Counters for Counters.Counter;

  address private academy_address;
  address private derace_contract;
  IERC721 private horse_token;
  IERC20  private derc_token;
  address private horse_token_address;
  address private derc_token_address;

  struct TenureData {
    Tenure.TenureState state;
    address horse_owner;
    uint64  horse_id;
    uint8   academy_mode;
    uint256 initial_derc_supply;
    uint256 current_derc_supply;
    uint256 start_time;
    uint256 tenure_length;
  }

  Counters.Counter private _tenure_id_counter;
  mapping(uint256 => address) private tenures;
  uint256[] private live_tenures;

  constructor () {}

  modifier onlyAcademy {
    require(msg.sender == academy_address, "unauthorized");
    _;
  }

  modifier onlyOwnerOrAcademy {
    require(msg.sender == academy_address || msg.sender == owner(), "unauthorized");
    _;
  }

  event TenureInitialized(
    uint256 tenure_id,
    address horse_owner,
    uint8 academy_mode,
    uint256 derc_requirement,
    uint256 tenure_length
  );

  event TenureSupplied(
    uint256 tenure_id,
    address horse_owner,
    uint64 horse_id,
    uint256 supplied_derc
  );

  event TenureActivated(
    uint256 tenure_id,
    uint8 academy_mode,
    uint256 start_time,
    uint256 tenure_length
  );

  event TenureCancelled(
    uint256 tenure_id,
    uint256 current_derc_supply
  );

  event TenureExpired(
    uint256 tenure_id,
    uint256 current_derc_supply
  );

  function set_roles (
    address _academy
  ) external onlyOwner {
    academy_address = _academy;
  }

  function set_external_contracts (
    address _derace_contract,
    address _horse_token,
    address _derc_token
  ) external onlyOwner {
    derace_contract = _derace_contract;
    horse_token = IERC721(_horse_token);
    horse_token_address = _horse_token;
    derc_token = IERC20(_derc_token);
    derc_token_address = _derc_token;
  }

  function initialize_tenure (
    address _horse_owner,
    uint8 _academy_mode,
    uint256 _initial_derc_supply,
    uint256 _tenure_length
  ) external onlyOwner {
    // require(_horse_owner != address(0), "invalid address");
    require(_initial_derc_supply >= 1 ether, "too little derc");
    require(_tenure_length > 0, "length too low");
    // require(derace_contract != address(0), "no external contracts");
    // require(derc_token_address != address(0), "no external contracts");

    uint256 new_tenure_id = _tenure_id_counter.current();
    _tenure_id_counter.increment();

    Tenure new_tenure = new Tenure(
      _horse_owner,
      _academy_mode,
      _initial_derc_supply,
      _tenure_length,
      derace_contract,
      derc_token_address,
      horse_token_address
    );

    tenures[new_tenure_id] = address(new_tenure);
    live_tenures.push(new_tenure_id);

    emit TenureInitialized(
      new_tenure_id,
      _horse_owner,
      _academy_mode,
      _initial_derc_supply,
      _tenure_length
    );

  }

  function get_tenure (uint256 tenure_id) external view returns(
    Tenure.TenureState state,
    address horse_owner,
    uint64 horse_id,
    uint8 academy_mode,
    uint256 initial_derc_supply,
    uint256 current_derc_supply,
    uint256 start_time,
    uint256 tenure_length
  ) {
    TenureData memory tenure_data;
    (
      tenure_data.state,
      tenure_data.horse_owner,
      tenure_data.horse_id,
      tenure_data.academy_mode,
      tenure_data.initial_derc_supply,
      tenure_data.current_derc_supply,
      tenure_data.start_time,
      tenure_data.tenure_length
    ) = Tenure(tenures[tenure_id]).get_tenure();
    require(msg.sender == tenure_data.horse_owner || msg.sender == owner(), "unauthorized");

    return (
      tenure_data.state,
      tenure_data.horse_owner,
      tenure_data.horse_id,
      tenure_data.academy_mode,
      tenure_data.initial_derc_supply,
      tenure_data.current_derc_supply,
      tenure_data.start_time,
      tenure_data.tenure_length
    );
  }

  function get_tenure_address (uint256 tenure_id) external onlyOwner view returns(
    address tenure_address
  ) {
    require(tenures[tenure_id] != address(0), "invalid");
    return tenures[tenure_id];
  }

  function get_all_tenures () external onlyOwner view returns(
    uint256[] memory all_tenures
  ) {
    return live_tenures;
  }

  function supply_tenure (
    uint256 tenure_id,
    uint64 _horse_id
  ) external {
    require(tenures[tenure_id] != address(0), "invalid");
    TenureData memory tenure_data;
    (
      tenure_data.state,
      tenure_data.horse_owner,
      tenure_data.horse_id,,
      tenure_data.initial_derc_supply,,,
    ) = Tenure(tenures[tenure_id]).get_tenure();

    require(tenure_data.state == Tenure.TenureState.Initialized, "wrong state");
    require(tenure_data.horse_owner == msg.sender, "unauthorized");
    // require(horse_token.ownerOf(_horse_id) == msg.sender, "wrong horse");
    // require(horse_token.getApproved(_horse_id) == address(this), "horse not approved");
    // require(derc_token.balanceOf(msg.sender) >= tenure_data.initial_derc_supply, "not enough DERC");
    // require(derc_token.allowance(msg.sender, address(this)) >= tenure_data.initial_derc_supply, "derc not approved");

    horse_token.transferFrom(msg.sender, tenures[tenure_id], _horse_id);
    derc_token.transferFrom(msg.sender, tenures[tenure_id], tenure_data.initial_derc_supply);

    assert(tenure_data.initial_derc_supply == derc_token.balanceOf(tenures[tenure_id]));
    assert(horse_token.ownerOf(_horse_id) == tenures[tenure_id]);

    Tenure(tenures[tenure_id]).supply(_horse_id);

    emit TenureSupplied(
      tenure_id,
      tenure_data.horse_owner,
      _horse_id,
      tenure_data.initial_derc_supply
    );
  }

  function activate_tenure (
    uint256 tenure_id
  ) external onlyOwner {
    require(tenures[tenure_id] != address(0), "invalid");
    TenureData memory tenure_data;
    (
      tenure_data.state,,,
      tenure_data.academy_mode,,,,
      tenure_data.tenure_length
    ) = Tenure(tenures[tenure_id]).get_tenure();
    require(tenure_data.state == Tenure.TenureState.Supplied, "wrong state");

    Tenure(tenures[tenure_id]).activate();

    emit TenureActivated(
      tenure_id,
      tenure_data.academy_mode,
      block.timestamp,
      tenure_data.tenure_length
    );
  }

  function register (
    uint256 tenure_id,
    uint256 race_id
  ) external onlyAcademy {
    require(tenures[tenure_id] != address(0), "invalid");
    TenureData memory tenure_data;
    (
      tenure_data.state,,,,,
      tenure_data.current_derc_supply,
      tenure_data.start_time,
      tenure_data.tenure_length
    ) = Tenure(tenures[tenure_id]).get_tenure();
    require(tenure_data.state != Tenure.TenureState.Paused, "paused");
    require(tenure_data.state == Tenure.TenureState.Active, "wrong state");

    Tenure tenure = Tenure(tenures[tenure_id]);
    if (tenure_data.start_time + tenure_data.tenure_length < block.timestamp) {
      tenure.cancel();
      emit TenureExpired(
        tenure_id,
        tenure_data.current_derc_supply
      );
    } else {
      tenure.register(race_id);
    }
  }

  function cancel_tenure (
    uint256 tenure_id
  ) public {
    require(tenures[tenure_id] != address(0), "invalid");
    TenureData memory tenure_data;
    (tenure_data.state, tenure_data.horse_owner ,,,,,,) = Tenure(tenures[tenure_id]).get_tenure();
    require(tenure_data.state != Tenure.TenureState.Cancelled, "wrong state");
    require(
      msg.sender == owner() ||
      msg.sender == tenure_data.horse_owner,
      "unauthorized"
    );

    Tenure(tenures[tenure_id]).cancel();

    emit TenureCancelled(
      tenure_id,
      tenure_data.current_derc_supply
    );
  }

  function set_tenure_paused (
    uint256 tenure_id,
    bool paused
  ) external onlyOwner {
    require(tenures[tenure_id] != address(0), "invalid");
    Tenure.TenureState state = Tenure(tenures[tenure_id]).get_state();
    require(
      (state == Tenure.TenureState.Active) ||
      (state == Tenure.TenureState.Paused)
    , "wrong state");

    Tenure(tenures[tenure_id]).set_paused(paused);
  }

  function set_tenure_academy_mode (
    uint256 tenure_id,
    uint8 academy_mode
  ) external onlyOwnerOrAcademy {
    require(tenures[tenure_id] != address(0), "invalid");
    Tenure(tenures[tenure_id]).set_academy_mode(academy_mode);
  }

  function deposit_funds_to_tenure (
    uint256 tenure_id,
    uint256 amount
  ) external {
    require(tenures[tenure_id] != address(0), "invalid");
    (Tenure.TenureState state, address horse_owner ,,,,,,) = Tenure(tenures[tenure_id]).get_tenure();
    require(
      (state == Tenure.TenureState.Supplied) ||
      (state == Tenure.TenureState.Active) ||
      (state == Tenure.TenureState.Paused)
    , "wrong state");
    require(
      msg.sender == owner() ||
      msg.sender == horse_owner,
      "unauthorized"
    );
    // require(derc_token.balanceOf(msg.sender) >= amount, "not enough DERC");
    // require(derc_token.allowance(msg.sender, address(this)) >= amount, "derc not approved");

    uint256 initial_contract_balance = derc_token.balanceOf(tenures[tenure_id]);
    derc_token.transferFrom(msg.sender, tenures[tenure_id], amount);
    assert(initial_contract_balance + amount == derc_token.balanceOf(tenures[tenure_id]));
    Tenure(tenures[tenure_id]).record_deposit(amount);
  }

  function withdraw_funds_from_tenure (
    uint256 tenure_id,
    uint256 amount,
    address to_address
  ) public onlyOwnerOrAcademy {
    require(tenures[tenure_id] != address(0), "invalid");
    Tenure.TenureState state = Tenure(tenures[tenure_id]).get_state();
    require(
      (state == Tenure.TenureState.Supplied) ||
      (state == Tenure.TenureState.Active) ||
      (state == Tenure.TenureState.Paused)
    , "wrong state");

    derc_token.transferFrom(tenures[tenure_id], to_address, amount);
  }
}


contract Tenure is Ownable {

  enum TenureState {
    Initialized,
    Supplied,
    Active,
    Paused,
    Cancelled
  }

  TenureState private state;
  address private horse_owner;
  uint64  private horse_id;
  uint8   private academy_mode;
  uint256 private initial_derc_supply;
  uint256 private start_time;
  uint256 private tenure_length;

  address private derace_contract;
  IERC20  private derc_token;
  IERC721 private horse_token;

  constructor (
    address _horse_owner,
    uint8 _academy_mode,
    uint256 _initial_derc_supply,
    uint256 _tenure_length,
    address _derace_contract,
    address _derc_token,
    address _horse_token
  ) {
    horse_owner = _horse_owner;
    academy_mode = _academy_mode;
    initial_derc_supply = _initial_derc_supply;
    tenure_length = _tenure_length;
    derace_contract = _derace_contract;
    derc_token = IERC20(_derc_token);
    horse_token = IERC721(_horse_token);

    derc_token.approve(derace_contract, 2**256 - 1);
    derc_token.approve(owner(), 2**256 - 1);
  }

  function get_tenure () external onlyOwner view returns(
    TenureState  _state,
    address  _horse_owner,
    uint64   _horse_id,
    uint8    _academy_mode,
    uint256  _initial_derc_supply,
    uint256  _current_derc_supply,
    uint256  _start_time,
    uint256  _tenure_length
  ) {
    return (
      state,
      horse_owner,
      horse_id,
      academy_mode,
      initial_derc_supply,
      derc_token.balanceOf(address(this)),
      start_time,
      tenure_length
    );
  }

  function get_state () external onlyOwner view returns(
    TenureState _state
  ) {
    return state;
  }

  function supply (
    uint64 _horse_id
  ) external onlyOwner {
    state = TenureState.Supplied;
    horse_id = _horse_id;
  }

  function activate () external onlyOwner {
    state = TenureState.Active;
    start_time = block.timestamp;
  }

  function register (
    uint256 race_id
  ) external onlyOwner {
    // uint256 initial_balance = derc_token.balanceOf(address(this));

    DummyDeRace(derace_contract).register(race_id, horse_id);

    // uint256 current_balance = derc_token.balanceOf(address(this));
    // used_derc += initial_balance - current_balance;
  }

  function cancel () external onlyOwner {
    if (state == TenureState.Initialized) {
      state = TenureState.Cancelled;
    } else {
      horse_token.transferFrom(address(this), horse_owner, horse_id);
      // assert(horse_token.ownerOf(horse_id) == horse_owner);
      state = TenureState.Cancelled;
    }
  }

  function set_paused (
    bool paused
  ) external onlyOwner {
    if (paused) {
      state = TenureState.Paused;
    } else {
      state = TenureState.Active;
    }
  }

  function set_academy_mode (
    uint8 _academy_mode
  ) external onlyOwner {
    academy_mode = _academy_mode;
    assert(academy_mode == _academy_mode);
  }

  function record_deposit (
    uint256 _amount
  ) external onlyOwner {
    initial_derc_supply += _amount;
    assert(initial_derc_supply - _amount < initial_derc_supply);
  }
}
